import React,{useState,useEffect} from 'react'
import styled from 'styled-components'
import {useThrottle} from '../utils/Throttle'

const SearchBarwrapper = styled.div`
position :relative;
border:1px solid black;
border-radius:20px;
display:flex;
flex-direction:row;
padding:10px;
width:300px;
margin-left:40%`

const IconImage = styled.img`
height:20px;
padding-right:20px;`


const RightSide = styled.div`
display:flex;
flex:0 0 auto;
padding-right:10px`


const Input = styled.input`
outline:none;
flex:100%;
border:none;
width:300px`

const Spinner = styled.div`
border:5px solid grey;
border-top: spx solid blue;
border-radius :50%;
width:15px;
height:15px;
animation: spin 1s linear infinite;
margin-left:15px;
@keyframes spin{
    0%{
        transform :rotate (0deg)
    }
    100%{
        transform : rotate(360deg)
    }
}`

const Suggestions = styled.div`
display:flex;
flex-direction:column;
flex:0 0 auto;
& *{
    border:1px solid black
}`



export default function SearchBar ({value,onChange,suggestions,loading,setLoading}){
    const [q,setQ] = useState("")
    const throttleval = useThrottle(q,1000)
    const handleInput =(e)=>{
        setQ(e.target.value)
        setLoading(true)
    }

    useEffect(()=>{
        onChange(throttleval)
    },[throttleval,onChange])


    const clearQuery =()=>{
        setQ("")
        if(loading){
            setLoading(false)
        }
    }
    return(
        <SearchBarwrapper>
            <IconImage src="https://image.flaticon.com/icons/svg/622/622669.svg"alt="icon"/>
            <Input value={q} onChange={handleInput}/>
            <RightSide>
                {q && <div onClick={clearQuery}>X</div>}
                {loading && <Spinner/>}
            </RightSide>
        </SearchBarwrapper>
    )
}
