import React,{useState,useEffect} from 'react'
import styled from 'styled-components'
import {useThrottle} from '../utils/Throttle'

const SearchBarwrapper = styled.div`
position :relative;
border:1px solid black;
border-radius:20px;
display:flex;
flex-direction:row;
padding:10px;
width:400px;
margin-left:35%;
border-bottom-right-radius :${({results})=>results?`0px`:`20px`}
border-bottom-left-radius :${({results})=>results?`0px`:`20px`}
border-bottom-color :${({results})=>results?`transparent`:`black`}`

const IconImage = styled.img`
height:20px;
padding-right:20px;`


const RightSide = styled.div`
display:flex;
flex:0 0 auto;
padding-right:10px`


const Input = styled.input`
outline:none;
flex:100%;
border:none;
width:300px`

const Spinner = styled.div`
border:5px solid grey;
border-top: 5px solid blue;
border-radius :50%;
width:15px;
height:15px;
animation: spin 1s linear infinite;
margin-left:15px;
@keyframes spin{
    0%{
        transform :rotate (0deg)
    }
    100%{
        transform : rotate(360deg)
    }
}`

const Suggestions = styled.div`
display:flex;
flex-direction:column;
flex:0 0 auto;
width:420px;
margin-left:35%;
max-height:300px;
overflow:scroll;
border-bottom-right-radius:20px;
border-bottom-left-radius:20px;

& *{
    border:1px solid grey;
    border-top-color:transparent;
    border-bottom-color:transparent;
    flex:1;
    padding:5px;
    padding-left:30px;


}
& :nth-child(${({active})=>active}){
    background:gray;
    color:white;
    font-weight:bolder
}`



export default function SearchBar ({value,onChange,suggestions,loading,setLoading}){
    const [q,setQ] = useState("")
    const [active,setActive]=useState(0)
    const throttleval = useThrottle(q,1000)
    const handleInput =(e)=>{
        setQ(e.target.value)
        setLoading(true)
    }

    useEffect(()=>{
        onChange(throttleval)
    },[throttleval,onChange])


    const clearQuery =()=>{
        setQ("")
        if(loading){
            setLoading(false)
        }
    }
    const handleSuggestions =(e)=>{
        switch(e.keyCode){
            case 40:{
                if(active >=suggestions.length){ //active+5
                    setActive(1)

                }
                else{
                    setActive((prev)=>prev+1)
                }
                return
            }
            case 38: {
                if(active ===1){
                    setActive(0)
                }

                else if(active <=0){
                    setActive(suggestions.length)
                }
                else{
                    setActive((prev)=>prev-1)
                }
                return 
            }
            case 13 :{
                //onEnter(Active-1)
            }
            default :{
                setActive(0)
                return
            }
        }

    }
    return(
        <>
        <SearchBarwrapper results={suggestions.length!==0 && !loading} onKeyUp={handleSuggestions}>
            <IconImage src="https://image.flaticon.com/icons/svg/622/622669.svg"alt="icon"/>
            <Input value={q} onChange={handleInput}/>
            <RightSide>
                {q && <div onClick={clearQuery}>X</div>}
                {loading && <Spinner/>}
            </RightSide>

        </SearchBarwrapper>

        {!loading && (
            <Suggestions active={active} limit={5}>
                {suggestions.filter((item,index)=>index >=active && index<active+5).map((item,index)=>(
                    <div key ={index} onMouseOver ={()=>setActive(index+1)}>{item}</div>

                ))}
            </Suggestions>
        )

        }

        </>
    )
}
