import React, { useState, useEffect } from 'react'
import styled from 'styled-components'
import { useThrottle } from '../utils/Throttle'

const SearchBarWrapper = styled.div`
position :relative;
border:1px solid black;
border-radius:20px;
display:flex;
flex-direction:row;
padding:10px;
width:400px;
margin-left:35%;
`
const IconImage = styled.img`
height:20px;
padding-right:20px;`

const Input = styled.input`
outline:none;
flex:100%;
border:none;
width:300px`

const RightSide = styled.div`
display:flex;
flex:0 0 auto;
padding-right:10px`



export default function Searchbar(onChange,suggestions,loading,setLoading) {
    const [q, setQ] = useState("")
    const [active, setActive] = useState(0)
    const throttleVal = useThrottle(q, 1000)
    const handelInput = (e) => {
        setQ(e.target.value)
        setLoading(false)
    }

    return (

        <>
        <SearchBarWrapper>
            <IconImage src="https://image.flaticon.com/icons/svg/622/622669.svg"alt="icon"/>
            <Input value={q} onChange = {handelInput}/>
            <RightSide>

            </RightSide>
        </SearchBarWrapper>

        </>
    )
}
